def calculate_median(numbers):
    if not numbers:
        return None

    # Manual sorting algorithm: Bubble Sort
    n = len(numbers)
    for i in range(n):
        for j in range(0, n-i-1):
            if numbers[j] > numbers[j+1]:
                numbers[j], numbers[j+1] = numbers[j+1], numbers[j]

    # Calculate median
    if n % 2 == 0:
        mid1 = numbers[n//2]
        mid2 = numbers[n//2 - 1]
        return (mid1 + mid2) / 2
    else:
        return numbers[n//2]

